mapper.select:

	SELECT userNo, blogTitle, logoFile
	FROM blog
	WHERE userNo = #{userNo}

mapper.update:

	UPDATE blog
	SET blogTitle = #{blogTitle}

	
		, logoFile = #{logoFile}
	

	WHERE userNo = #{userNo}

mapper.select:

	SELECT count(cateNo)
	FROM category
	WHERE userNo = #{userNo}
